const requirements=$json.requirements||''; const languages=$json.languages||['python','javascript','java','csharp','go']; const project=$json.project||{name:'app'}; if(!requirements) throw new Error('requirements is required');
const scaffolds=languages.map(l=>({ language:l, structure: l==='python'?['src/','tests/','pyproject.toml'] : l==='javascript'?['src/','tests/','package.json'] : l==='java'?['src/main/java','src/test/java','pom.xml'] : l==='csharp'?['src/','tests/','.csproj'] : l==='go'?['cmd/','pkg/','go.mod'] : ['src/','tests/'] }));
const plan={ objectives: ['Satisfy requirements','Apply TDD','Ensure security best practices'], languages, project, scaffolds, guidelines: { code_style: 'lint + format', security: ['input validation','secrets management','dependency scan'] } };
const starterFiles=scaffolds.reduce((acc,s)=>{acc[s.language]=['README.md']; return acc;},{});
return [{ json: { codegen: { plan, starterFiles } } }];
